import axios from 'axios'
import React from 'react'
import { Redirect } from 'react-router-dom'
import Navbar from './Navbar'

class Register extends React.Component {
    constructor() {
        super()
        this.state = {
            name: '',
            email: '',
            password: '',
            confirm_password: '',
            redirect: false,
            errors: []
        }
    }

    handleNameChange = event => {
        this.setState({name: event.target.value}, () => {
            console.log(this.state)
        })
    }
    handleEmailChange = event => {
        this.setState({email: event.target.value}, () => {
            console.log(this.state)
        })
    }
    handlePasswordChange = event => {
        this.setState({password: event.target.value}, () => {
            console.log(this.state)
        })
    }
    handleConfirmPasswordChange = event => {
        this.setState({confirm_password: event.target.value}, () => {
            console.log(this.state)
        })
    }

    handleSubmit = event =>{
        event.preventDefault()

        let bodyFormData = new FormData()
        bodyFormData.set('name', this.state.name)
        bodyFormData.set('email', this.state.email)
        bodyFormData.set('password', this.state.password)
        bodyFormData.set('confirm_password', this.state.confirm_password)

        axios.post('http://127.0.0.1:8000/api/register', bodyFormData)
            .then(res => {
                console.log(res.data)
                localStorage.setItem('token', res.data.api_token)
                this.setState({ redirect: true })
            })
            .catch(error => {
                if(error.response.status === 401) {
                    this.setState({errors : error.response.data.errors}, () => {
                        console.log(this.state)
                    })
                }
            })
    }

    render() {
        if(this.state.redirect) {
            return (<Redirect to='/'/>)
        }
        return (
            <>
                <Navbar />
                <div class="container w-50">
                    <h2 class="text-center my-5">Inscription</h2>
                    <form method="POST" onSubmit={this.handleSubmit}>
                        <div class="mb-3">
                            <label for="exampleInputEmail1" class="form-label">Nom</label>
                            <input onChange={this.handleNameChange} type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                            { this.state.errors && this.state.errors.name ? <div class="text-danger invalide-feedback">{ this.state.errors['name'] }</div> : '' }
                        </div>
                        <div class="mb-3">
                            <label for="exampleInputEmail1" class="form-label">Adresse email</label>
                            <input onChange={this.handleEmailChange} type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                            <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
                        </div>
                        <div class="mb-3">
                            <label for="exampleInputPassword1" class="form-label">Mot de passe</label>
                            <input onChange={this.handlePasswordChange} type="password" class="form-control" id="exampleInputPassword1" />
                        </div>
                        <div class="mb-3">
                            <label for="exampleInputPassword1" class="form-label">Confirmez le mot de passe</label>
                            <input onChange={this.handleConfirmPasswordChange} type="password" class="form-control" id="exampleInputPassword1" />
                        </div>
                        <button type="submit" class="btn btn-primary">Submit</button>
                    </form>
                </div>
            </>
        )
    }
}

export default Register
